#pragma once#include "Model.h"#include "ResourceManager.h"#include <set>class RenderEngine{public:	RenderEngine();	~RenderEngine();	void addModel(Model * model);	void removeModel(Model * model);	void createModel();	void render() const;	ResourceManager<Texture> & getTextureManager();	ResourceManager<Shader> & getShaderManager();	RenderEngine(RenderEngine const &) = delete;	RenderEngine & operator =(RenderEngine const &) = delete;private:	class ModelCompare	{	public:		bool operator ()(Model const * model0, Model const * model1)		{			return *model0 < *model1;		}	};	std::set<Model *, ModelCompare> mModels;	ResourceManager<Texture> mTextureManager;	ResourceManager<Shader> mShaderManager;	// ShaderManager	// TextureManager	// FontManager};