#pragma once#include "Model.h"#include "ResourceManager.h"#include <set>class RenderEngine{public:	RenderEngine();	~RenderEngine();	void createModel();	void render() const;	RenderEngine(RenderEngine const &) = delete;	RenderEngine & operator =(RenderEngine const &) = delete;private:	class ModelCompare	{	public:		bool operator ()(Model const * model0, Model const * model1)		{			return *model0 < *model1;		}	};	void addModel(Model * model);	void removeModel(Model * model);	std::set<Model *, ModelCompare> mModels;	ResourceManager<Texture, std::string const &> mTextureManager;	ResourceManager<Shader, std::string const [Shader::NumTypes]> mShaderManager;	// ShaderManager	// TextureManager	// FontManager};